import React, { useState, useEffect } from 'react';
import axios from 'axios';

function TransactionForm() {
  const [formData, setFormData] = useState({
    step: 1,
    type: "PAYMENT",
    amount: 1000,
    oldbalanceOrg: 10000,
    newbalanceOrig: 9000,
    oldbalanceDest: 5000,
    newbalanceDest: 6000
  });
  
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  
  const containerStyles = {
    maxWidth: '1200px',
    margin: '0 auto'
  };
  
  const formResultContainerStyles = {
    display: 'grid',
    gridTemplateColumns: '1fr 1fr',
    gap: '30px',
    marginTop: '20px'
  };
  
  const formContainerStyles = {
    backgroundColor: 'white',
    borderRadius: '8px',
    padding: '20px',
    boxShadow: '0 2px 10px rgba(0, 0, 0, 0.05)'
  };
  
  const formStyles = {
    display: 'grid',
    gridGap: '15px'
  };
  
  const formGroupStyles = {
    marginBottom: '15px'
  };
  
  const labelStyles = {
    display: 'block',
    marginBottom: '5px',
    fontWeight: '500'
  };
  
  const inputStyles = {
    width: '100%',
    padding: '10px',
    borderRadius: '4px',
    border: '1px solid #ddd',
    fontSize: '16px'
  };
  
  const selectStyles = {
    ...inputStyles,
    height: '40px'
  };
  
  const sampleButtonStyles = {
    padding: '10px 15px',
    backgroundColor: '#2c3e50',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    marginBottom: '20px'
  };
  
  const submitButtonStyles = {
    padding: '12px',
    backgroundColor: '#3498db',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    fontSize: '16px',
    cursor: 'pointer',
    marginTop: '15px'
  };
  
  const resultContainerStyles = {
    backgroundColor: 'white',
    borderRadius: '8px',
    padding: '20px',
    boxShadow: '0 2px 10px rgba(0, 0, 0, 0.05)'
  };
  
  const resultHeaderStyles = {
    display: 'grid',
    gridTemplateColumns: '1fr 1fr 1fr',
    gap: '15px',
    marginBottom: '20px'
  };
  
  const probabilityGaugeStyles = {
    height: '24px',
    backgroundColor: '#f0f0f0',
    borderRadius: '12px',
    position: 'relative',
    overflow: 'hidden',
    marginTop: '10px'
  };
  
  const probabilityFillStyles = (probability) => ({
    height: '100%',
    width: `${probability * 100}%`,
    backgroundColor: probability > 0.7 ? '#e74c3c' : probability > 0.2 ? '#f39c12' : '#2ecc71',
    borderRadius: '12px'
  });
  
  const probabilityValueStyles = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    color: '#333',
    fontWeight: '600'
  };
  
  const explanationStyles = {
    backgroundColor: '#f9f9f9',
    padding: '15px',
    borderRadius: '4px',
    whiteSpace: 'pre-wrap',
    fontFamily: 'monospace'
  };
  
  const loadSampleTransaction = () => {
    setLoading(true);
    axios.get('http://localhost:5000/api/test-transaction')
      .then(response => {
        setFormData(response.data);
        setLoading(false);
      })
      .catch(err => {
        setError(err.message);
        setLoading(false);
      });
  };
  
  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);
    setResult(null);
    setError(null);
    
    axios.post('http://localhost:5000/api/predict', formData)
      .then(response => {
        setResult(response.data);
        setLoading(false);
      })
      .catch(err => {
        setError(err.response?.data?.error || err.message);
        setLoading(false);
      });
  };
  
  const handleChange = (e) => {
    const { name, value } = e.target;
    const numericFields = ['step', 'amount', 'oldbalanceOrg', 'newbalanceOrig', 'oldbalanceDest', 'newbalanceDest'];
    const newValue = numericFields.includes(name) ? parseFloat(value) : value;
    
    setFormData({
      ...formData,
      [name]: newValue,
    });
  };
  
  useEffect(() => {
    if (formData.type === "PAYMENT" || formData.type === "TRANSFER" || formData.type === "CASH_OUT") {
      setFormData(prev => ({
        ...prev,
        newbalanceOrig: Math.max(0, prev.oldbalanceOrg - prev.amount)
      }));
    } else if (formData.type === "CASH_IN") {
      setFormData(prev => ({
        ...prev,
        newbalanceOrig: prev.oldbalanceOrg + prev.amount
      }));
    }
  }, [formData.amount, formData.oldbalanceOrg, formData.type]);
  
  useEffect(() => {
    if (formData.type === "PAYMENT" || formData.type === "TRANSFER" || formData.type === "CASH_IN") {
      setFormData(prev => ({
        ...prev,
        newbalanceDest: prev.oldbalanceDest + prev.amount
      }));
    } else if (formData.type === "CASH_OUT") {
      setFormData(prev => ({
        ...prev,
        newbalanceDest: Math.max(0, prev.oldbalanceDest - prev.amount)
      }));
    }
  }, [formData.amount, formData.oldbalanceDest, formData.type]);
  
  return (
    <div style={containerStyles}>
      <h1>Test Fraud Detection</h1>
      <p>Fill out the form below to test the fraud detection system with a transaction.</p>
      
      <div style={formResultContainerStyles}>
        <div style={formContainerStyles}>
          <button 
            type="button" 
            onClick={loadSampleTransaction} 
            style={sampleButtonStyles}
          >
            Load Sample Transaction
          </button>
          
          <form onSubmit={handleSubmit} style={formStyles}>
            <div style={formGroupStyles}>
              <label htmlFor="type" style={labelStyles}>Transaction Type</label>
              <select
                id="type"
                name="type"
                value={formData.type}
                onChange={handleChange}
                required
                style={selectStyles}
              >
                <option value="PAYMENT">Payment</option>
                <option value="TRANSFER">Transfer</option>
                <option value="CASH_OUT">Cash Out</option>
                <option value="CASH_IN">Cash In</option>
                <option value="DEBIT">Debit</option>
              </select>
            </div>
            
            <div style={formGroupStyles}>
              <label htmlFor="amount" style={labelStyles}>Amount</label>
              <input
                type="number"
                id="amount"
                name="amount"
                value={formData.amount}
                onChange={handleChange}
                min="0"
                step="0.01"
                required
                style={inputStyles}
              />
            </div>
            
            <div style={formGroupStyles}>
              <label htmlFor="oldbalanceOrg" style={labelStyles}>Origin Account Initial Balance</label>
              <input
                type="number"
                id="oldbalanceOrg"
                name="oldbalanceOrg"
                value={formData.oldbalanceOrg}
                onChange={handleChange}
                min="0"
                step="0.01"
                required
                style={inputStyles}
              />
            </div>
            
            <div style={formGroupStyles}>
              <label htmlFor="newbalanceOrig" style={labelStyles}>Origin Account New Balance</label>
              <input
                type="number"
                id="newbalanceOrig"
                name="newbalanceOrig"
                value={formData.newbalanceOrig}
                onChange={handleChange}
                min="0"
                step="0.01"
                required
                style={{...inputStyles, backgroundColor: '#f9f9f9'}}
                readOnly
              />
            </div>
            
            <div style={formGroupStyles}>
              <label htmlFor="oldbalanceDest" style={labelStyles}>Destination Account Initial Balance</label>
              <input
                type="number"
                id="oldbalanceDest"
                name="oldbalanceDest"
                value={formData.oldbalanceDest}
                onChange={handleChange}
                min="0"
                step="0.01"
                required
                style={inputStyles}
              />
            </div>
            
            <div style={formGroupStyles}>
              <label htmlFor="newbalanceDest" style={labelStyles}>Destination Account New Balance</label>
              <input
                type="number"
                id="newbalanceDest"
                name="newbalanceDest"
                value={formData.newbalanceDest}
                onChange={handleChange}
                min="0"
                step="0.01"
                required
                style={{...inputStyles, backgroundColor: '#f9f9f9'}}
                readOnly
              />
            </div>
            
            <div style={formGroupStyles}>
              <label htmlFor="step" style={labelStyles}>Time Step</label>
              <input
                type="number"
                id="step"
                name="step"
                value={formData.step}
                onChange={handleChange}
                min="1"
                required
                style={inputStyles}
              />
            </div>
            
            <button 
              type="submit" 
              style={submitButtonStyles} 
              disabled={loading}
            >
              {loading ? 'Processing...' : 'Check for Fraud'}
            </button>
          </form>
        </div>
        
        <div style={resultContainerStyles}>
          {loading && <div style={{ textAlign: 'center', padding: '20px' }}>Processing transaction...</div>}
          
          {error && <div style={{ backgroundColor: '#fceaea', border: '1px solid #e74c3c', padding: '10px', borderRadius: '4px', color: '#e74c3c' }}>Error: {error}</div>}
          
          {result && (
            <div>
              <h2>Detection Result</h2>
              
              <div style={resultHeaderStyles}>
                <div>
                  <h3>Decision</h3>
                  <p>{result.decision}</p>
                </div>
                
                <div>
                  <h3>Fraud Probability</h3>
                  <div style={probabilityGaugeStyles}>
                    <div style={probabilityFillStyles(result.probability)}></div>
                    <span style={probabilityValueStyles}>{(result.probability * 100).toFixed(2)}%</span>
                  </div>
                </div>
                
                <div>
                  <h3>Recommended Action</h3>
                  <p>{result.action.replace('_', ' ').toUpperCase()}</p>
                </div>
              </div>
              
              <div>
                <h3>Explanation</h3>
                <pre style={explanationStyles}>{result.explanation}</pre>
              </div>
            </div>
          )}
          
          {!loading && !error && !result && (
            <div style={{ textAlign: 'center', padding: '40px 20px' }}>
              <p>Submit a transaction to see fraud detection results.</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default TransactionForm;
