import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

function Dashboard() {
  const navigate = useNavigate(); // Add useNavigate hook for programmatic navigation
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [recentTransactions, setRecentTransactions] = useState([]);
  const [apiStatus, setApiStatus] = useState({ stats: 'loading', transactions: 'loading' });

  const dashboardStyles = {
    maxWidth: '1200px',
    margin: '0 auto'
  };

  const statsOverviewStyles = {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
    gap: '20px',
    marginBottom: '30px'
  };

  const statCardStyles = {
    backgroundColor: 'white',
    borderRadius: '8px',
    padding: '20px',
    boxShadow: '0 2px 10px rgba(0, 0, 0, 0.05)',
    transition: 'transform 0.3s, box-shadow 0.3s'
  };

  const statValueStyles = {
    fontSize: '32px',
    fontWeight: '600',
    color: '#3498db'
  };

  const sectionHeaderStyles = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '15px'
  };

  const viewAllStyles = {
    color: '#3498db',
    textDecoration: 'none',
    fontSize: '14px'
  };

  const transactionsContainerStyles = {
    backgroundColor: 'white',
    borderRadius: '8px',
    padding: '20px',
    boxShadow: '0 2px 10px rgba(0, 0, 0, 0.05)',
    marginBottom: '30px'
  };

  const tableStyles = {
    width: '100%',
    borderCollapse: 'collapse'
  };

  const tableHeaderStyles = {
    padding: '12px',
    textAlign: 'left',
    borderBottom: '1px solid #eee',
    backgroundColor: '#f9f9f9',
    fontWeight: '600'
  };

  const tableCellStyles = {
    padding: '12px',
    textAlign: 'left',
    borderBottom: '1px solid #eee'
  };

  const fraudRowStyles = {
    backgroundColor: 'rgba(231, 76, 60, 0.1)',
    cursor: 'pointer' // Add cursor pointer to indicate clickable
  };

  const highRiskRowStyles = {
    backgroundColor: 'rgba(243, 156, 18, 0.1)',
    cursor: 'pointer' // Add cursor pointer to indicate clickable
  };

  const transactionRowStyles = {
    cursor: 'pointer' // Base style for all transaction rows
  };

  const quickActionsStyles = {
    backgroundColor: 'white',
    borderRadius: '8px',
    padding: '20px',
    boxShadow: '0 2px 10px rgba(0, 0, 0, 0.05)'
  };

  const actionButtonsStyles = {
    display: 'flex',
    gap: '15px'
  };

  const actionButtonStyles = {
    display: 'inline-block',
    padding: '12px 20px',
    backgroundColor: '#3498db',
    color: 'white',
    borderRadius: '4px',
    textDecoration: 'none',
    fontWeight: '500',
    transition: 'background-color 0.3s'
  };

  const errorStyles = {
    backgroundColor: '#fceaea',
    border: '1px solid #e74c3c',
    padding: '10px',
    borderRadius: '4px',
    color: '#e74c3c',
    marginBottom: '20px'
  };

  const warningStyles = {
    backgroundColor: '#fef9e7',
    border: '1px solid #f39c12',
    padding: '10px',
    borderRadius: '4px',
    color: '#9a7d0a',
    marginBottom: '20px'
  };

  useEffect(() => {
    // Check if API server is available
    axios.get('http://localhost:5000')
      .then(response => {
        console.log('API server is running:', response.data);
      })
      .catch(err => {
        setError(`Backend server may not be running: ${err.message}`);
        setApiStatus({
          stats: 'error',
          transactions: 'error'
        });
        setLoading(false);
        return;
      });

    // Fetch statistics
    axios.get('http://localhost:5000/api/statistics')
      .then(response => {
        console.log('Statistics loaded successfully');
        setStats(response.data);
        setApiStatus(prev => ({...prev, stats: 'loaded'}));
        if (prev => prev.transactions !== 'loading') {
          setLoading(false);
        }
      })
      .catch(err => {
        console.error('Error fetching statistics:', err);
        const errorMessage = err.response ? 
          `Error fetching statistics: ${err.response.status} ${err.response.statusText}` : 
          `Error fetching statistics: ${err.message}`;
        
        setApiStatus(prev => ({...prev, stats: 'error'}));
        setError(errorMessage);
        setLoading(false);
      });

    // Fetch recent transactions
    axios.get('http://localhost:5000/api/transactions', {
      params: { limit: 5 }
    })
      .then(response => {
        console.log('Transactions loaded successfully');
        // Make sure transactions is an array
        const transactions = Array.isArray(response.data.transactions) 
          ? response.data.transactions 
          : [];
        setRecentTransactions(transactions);
        setApiStatus(prev => ({...prev, transactions: 'loaded'}));
        if (prev => prev.stats !== 'loading') {
          setLoading(false);
        }
      })
      .catch(err => {
        console.error('Error fetching recent transactions:', err);
        const errorMessage = err.response ? 
          `Error fetching transactions: ${err.response.status} ${err.response.statusText}` : 
          `Error fetching transactions: ${err.message}`;
        
        setApiStatus(prev => ({...prev, transactions: 'error'}));
        // Don't set error state here to allow showing the dashboard even if transactions fail
      });
  }, []);

  // Function to handle transaction row click
  const handleTransactionClick = (tx) => {
    navigate('/history', { state: { transactionId: tx.id } });
  };

  // Show full screen loading
  if (loading && apiStatus.stats === 'loading' && apiStatus.transactions === 'loading') {
    return (
      <div style={{ textAlign: 'center', padding: '20px', fontSize: '18px', color: '#3498db' }}>
        Loading dashboard data...
      </div>
    );
  }

  // If there's a critical error, show only that
  if (error && (apiStatus.stats === 'error' && apiStatus.transactions === 'error')) {
    return (
      <div style={errorStyles}>
        <h2>Error</h2>
        <p>{error}</p>
        <p>Make sure your backend server is running at http://localhost:5000</p>
        <div style={{ marginTop: '20px' }}>
          <button 
            onClick={() => window.location.reload()} 
            style={actionButtonStyles}
          >
            Retry
          </button>
        </div>
      </div>
    );
  }

  return (
    <div style={dashboardStyles}>
      <h1>Fraud Detection Dashboard</h1>
      
      {/* Show errors if any */}
      {error && <div style={errorStyles}>{error}</div>}
      
      {/* Show warning for partial data */}
      {(apiStatus.stats === 'error' || apiStatus.transactions === 'error') && (
        <div style={warningStyles}>
          <p><strong>Warning:</strong> Some data could not be loaded. Dashboard may show incomplete information.</p>
          {apiStatus.stats === 'error' && <p>Statistics data unavailable.</p>}
          {apiStatus.transactions === 'error' && <p>Transaction data unavailable.</p>}
        </div>
      )}
      
      {/* Statistics Overview */}
      {apiStatus.stats === 'loaded' && stats && (
        <div style={statsOverviewStyles}>
          <div style={statCardStyles}>
            <h3>Total Transactions</h3>
            <p style={statValueStyles}>{stats.total_transactions || 0}</p>
          </div>
          
          <div style={statCardStyles}>
            <h3>Fraud Rate</h3>
            <p style={statValueStyles}>
              {stats.prediction_counts && stats.prediction_counts['🚨 CONFIRMED_FRAUD'] && stats.total_transactions ? 
                ((stats.prediction_counts['🚨 CONFIRMED_FRAUD'] / stats.total_transactions) * 100).toFixed(2) + '%' : 
                '0%'}
            </p>
          </div>
          
          <div style={statCardStyles}>
            <h3>High Risk</h3>
            <p style={statValueStyles}>
              {stats.prediction_counts && stats.prediction_counts['⚠️ HIGH_RISK'] ? 
                stats.prediction_counts['⚠️ HIGH_RISK'] : 
                '0'}
            </p>
          </div>
          
          <div style={statCardStyles}>
            <h3>Average Risk Score</h3>
            <p style={statValueStyles}>
              {stats.average_probability !== undefined ? 
                (stats.average_probability * 100).toFixed(2) + '%' : 
                '0%'}
            </p>
          </div>
        </div>
      )}
      
      {/* Transactions Table */}
      {apiStatus.transactions !== 'error' && (
        <div style={transactionsContainerStyles}>
          <div style={sectionHeaderStyles}>
            <h2>Recent Transactions</h2>
            <Link to="/history" style={viewAllStyles}>View All</Link>
          </div>
          
          {recentTransactions.length === 0 ? (
            <p>No transactions yet. Try analyzing a test transaction.</p>
          ) : (
            <table style={tableStyles}>
              <thead>
                <tr>
                  <th style={tableHeaderStyles}>ID</th>
                  <th style={tableHeaderStyles}>Type</th>
                  <th style={tableHeaderStyles}>Amount</th>
                  <th style={tableHeaderStyles}>Result</th>
                  <th style={tableHeaderStyles}>Probability</th>
                  <th style={tableHeaderStyles}>Time</th>
                </tr>
              </thead>
              <tbody>
                {recentTransactions.map(tx => {
                  // Safety checks for transaction properties
                  if (!tx || !tx.transaction_data) {
                    return null;
                  }
                  
                  // Determine row style with hover effect
                  let rowStyle = {...transactionRowStyles};
                  if (tx.prediction && tx.prediction.includes('FRAUD')) {
                    rowStyle = {...fraudRowStyles};
                  } else if (tx.prediction && tx.prediction.includes('HIGH_RISK')) {
                    rowStyle = {...highRiskRowStyles};
                  }
                  
                  return (
                    <tr 
                      key={tx.id} 
                      style={rowStyle}
                      onClick={() => handleTransactionClick(tx)}
                    >
                      <td style={tableCellStyles}>{tx.id}</td>
                      <td style={tableCellStyles}>{tx.transaction_data.type || 'Unknown'}</td>
                      <td style={tableCellStyles}>
                        ${typeof tx.transaction_data.amount === 'number' 
                          ? tx.transaction_data.amount.toLocaleString() 
                          : '0'}
                      </td>
                      <td style={tableCellStyles}>{tx.prediction || 'Unknown'}</td>
                      <td style={tableCellStyles}>
                        {typeof tx.probability === 'number' 
                          ? (tx.probability * 100).toFixed(2) + '%' 
                          : '0%'}
                      </td>
                      <td style={tableCellStyles}>
                        {tx.timestamp 
                          ? new Date(tx.timestamp).toLocaleString() 
                          : 'Unknown'}
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          )}
        </div>
      )}
      
      {/* Quick Actions */}
      <div style={quickActionsStyles}>
        <h2>Quick Actions</h2>
        <div style={actionButtonsStyles}>
          <Link to="/test-transaction" style={actionButtonStyles}>
            Test New Transaction
          </Link>
          <Link to="/location" style={actionButtonStyles}>
            Location Analysis
          </Link>
          <Link to="/statistics" style={actionButtonStyles}>
            Statistics
          </Link>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
