import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';

function TransactionHistory() {
  const [transactions, setTransactions] = useState([]);
  const [totalTransactions, setTotalTransactions] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedTransaction, setSelectedTransaction] = useState(null);
  const [filters, setFilters] = useState({
    prediction: '',
    country: '',
    limit: 20,
    offset: 0
  });

  const location = useLocation();
  const navigate = useNavigate();

  // Container styles
  const containerStyles = {
    maxWidth: '1200px',
    margin: '0 auto'
  };

  const transactionsContainerStyles = {
    backgroundColor: 'white',
    borderRadius: '8px',
    padding: '20px',
    boxShadow: '0 2px 10px rgba(0, 0, 0, 0.05)',
    marginBottom: '30px'
  };

  const tableStyles = {
    width: '100%',
    borderCollapse: 'collapse'
  };

  const tableHeaderStyles = {
    padding: '12px',
    textAlign: 'left',
    borderBottom: '1px solid #eee',
    backgroundColor: '#f9f9f9',
    fontWeight: '600'
  };

  const tableCellStyles = {
    padding: '12px',
    textAlign: 'left',
    borderBottom: '1px solid #eee'
  };

  const fraudRowStyles = {
    backgroundColor: 'rgba(231, 76, 60, 0.1)'
  };

  const highRiskRowStyles = {
    backgroundColor: 'rgba(243, 156, 18, 0.1)'
  };

  const detailsContainerStyles = {
    backgroundColor: 'white',
    borderRadius: '8px',
    padding: '20px',
    boxShadow: '0 2px 10px rgba(0, 0, 0, 0.05)',
    marginBottom: '30px'
  };

  const detailsGridStyles = {
    display: 'grid',
    gridTemplateColumns: 'repeat(2, 1fr)',
    gap: '20px',
    marginBottom: '20px'
  };

  const detailItemStyles = {
    backgroundColor: '#f9f9f9',
    padding: '15px',
    borderRadius: '8px'
  };

  const explanationStyles = {
    backgroundColor: '#f9f9f9',
    padding: '15px',
    borderRadius: '4px',
    whiteSpace: 'pre-wrap',
    fontFamily: 'monospace'
  };

  const paginationStyles = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginTop: '20px'
  };

  const paginationButtonStyles = {
    padding: '8px 12px',
    backgroundColor: '#3498db',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer'
  };

  const disabledButtonStyles = {
    ...paginationButtonStyles,
    backgroundColor: '#cccccc',
    cursor: 'not-allowed'
  };

  const filtersContainerStyles = {
    display: 'flex',
    gap: '15px',
    marginBottom: '20px'
  };

  const filterSelectStyles = {
    padding: '8px 12px',
    borderRadius: '4px',
    border: '1px solid #ddd'
  };

  const refreshButtonStyles = {
    padding: '8px 12px',
    backgroundColor: '#2c3e50',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer'
  };

  // Check if a transaction ID was passed via location state
  useEffect(() => {
    if (location.state && location.state.transactionId) {
      // If a transaction ID is provided, fetch and select that transaction
      fetchTransactionDetails(location.state.transactionId);
    } else {
      // Otherwise, load all transactions
      fetchTransactions();
    }
  }, [location.state]);

  // Fetch all transactions with pagination and filters
  const fetchTransactions = () => {
    setLoading(true);
    setError(null);

    const queryParams = new URLSearchParams();
    queryParams.append('limit', filters.limit);
    queryParams.append('offset', filters.offset);
    
    if (filters.prediction) {
      queryParams.append('prediction', filters.prediction);
    }
    
    if (filters.country) {
      queryParams.append('country', filters.country);
    }

    axios.get(`http://localhost:5000/api/transactions?${queryParams.toString()}`)
      .then(response => {
        setTransactions(response.data.transactions || []);
        setTotalTransactions(response.data.total || 0);
        setLoading(false);
      })
      .catch(err => {
        console.error('Error fetching transactions:', err);
        setError(`Error fetching transactions: ${err.message}`);
        setLoading(false);
      });
  };

  // Fetch a specific transaction by ID
  const fetchTransactionDetails = (transactionId) => {
    setLoading(true);
    setError(null);

    axios.get(`http://localhost:5000/api/transactions?limit=1000`)
      .then(response => {
        const transactions = response.data.transactions || [];
        const transaction = transactions.find(tx => tx.id === parseInt(transactionId));
        
        if (transaction) {
          setSelectedTransaction(transaction);
          setTransactions(transactions);
          setTotalTransactions(response.data.total || 0);
        } else {
          setError(`Transaction with ID ${transactionId} not found.`);
        }
        
        setLoading(false);
      })
      .catch(err => {
        console.error('Error fetching transaction details:', err);
        setError(`Error fetching transaction details: ${err.message}`);
        setLoading(false);
      });
  };

  // Handle filter changes
  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters({
      ...filters,
      [name]: value,
      offset: 0 // Reset to first page when filters change
    });
  };

  // Apply filters when button is clicked
  const applyFilters = () => {
    fetchTransactions();
  };

  // Handle pagination
  const handlePrevPage = () => {
    if (filters.offset - filters.limit >= 0) {
      setFilters({
        ...filters,
        offset: filters.offset - filters.limit
      });
    }
  };

  const handleNextPage = () => {
    if (filters.offset + filters.limit < totalTransactions) {
      setFilters({
        ...filters,
        offset: filters.offset + filters.limit
      });
    }
  };

  // Update transactions when filters change
  useEffect(() => {
    fetchTransactions();
  }, [filters.offset, filters.limit]);

  // Handle row click to show transaction details
  const handleRowClick = (transaction) => {
    setSelectedTransaction(transaction);
    // Update URL without reloading
    navigate(`/history?id=${transaction.id}`, { replace: true });
    // Scroll to the top of the page
    window.scrollTo(0, 0);
  };

  // Format date for display
  const formatDate = (dateString) => {
    if (!dateString) return 'Unknown';
    
    try {
      const date = new Date(dateString);
      return date.toLocaleString();
    } catch (e) {
      return dateString;
    }
  };

  return (
    <div style={containerStyles}>
      <h1>Transaction History</h1>
      
      {/* Filters */}
      <div style={filtersContainerStyles}>
        <select 
          name="prediction" 
          value={filters.prediction} 
          onChange={handleFilterChange}
          style={filterSelectStyles}
        >
          <option value="">All Predictions</option>
          <option value="FRAUD">Fraud</option>
          <option value="HIGH_RISK">High Risk</option>
          <option value="NEEDS_REVIEW">Needs Review</option>
          <option value="LEGITIMATE">Legitimate</option>
        </select>
        
        <select 
          name="country" 
          value={filters.country} 
          onChange={handleFilterChange}
          style={filterSelectStyles}
        >
          <option value="">All Countries</option>
          <option value="US">United States</option>
          <option value="CA">Canada</option>
          <option value="GB">United Kingdom</option>
          <option value="FR">France</option>
          <option value="DE">Germany</option>
          <option value="JP">Japan</option>
          <option value="CN">China</option>
          <option value="AU">Australia</option>
          <option value="IN">India</option>
          <option value="BR">Brazil</option>
        </select>
        
        <select 
          name="limit" 
          value={filters.limit} 
          onChange={handleFilterChange}
          style={filterSelectStyles}
        >
          <option value="10">10 per page</option>
          <option value="20">20 per page</option>
          <option value="50">50 per page</option>
          <option value="100">100 per page</option>
        </select>
        
        <button 
          onClick={applyFilters} 
          style={refreshButtonStyles}
        >
          Apply Filters
        </button>
      </div>
      
      {/* Transaction Details */}
      {selectedTransaction && (
        <div style={detailsContainerStyles}>
          <h2>Transaction Details</h2>
          
          <div style={detailsGridStyles}>
            <div style={detailItemStyles}>
              <h3>Basic Information</h3>
              <p><strong>ID:</strong> {selectedTransaction.id}</p>
              <p><strong>Type:</strong> {selectedTransaction.transaction_data?.type || 'Unknown'}</p>
              <p><strong>Amount:</strong> ${selectedTransaction.transaction_data?.amount?.toLocaleString() || '0'}</p>
              <p><strong>Time:</strong> {formatDate(selectedTransaction.timestamp)}</p>
            </div>
            
            <div style={detailItemStyles}>
              <h3>Fraud Detection Result</h3>
              <p><strong>Decision:</strong> {selectedTransaction.prediction || 'Unknown'}</p>
              <p><strong>Probability:</strong> {(selectedTransaction.probability * 100).toFixed(2)}%</p>
              <p><strong>Action:</strong> {selectedTransaction.action?.replace('_', ' ').toUpperCase() || 'Unknown'}</p>
            </div>
            
            <div style={detailItemStyles}>
              <h3>Account Information</h3>
              <p><strong>Origin Initial Balance:</strong> ${selectedTransaction.transaction_data?.oldbalanceOrg?.toLocaleString() || '0'}</p>
              <p><strong>Origin New Balance:</strong> ${selectedTransaction.transaction_data?.newbalanceOrig?.toLocaleString() || '0'}</p>
              <p><strong>Destination Initial Balance:</strong> ${selectedTransaction.transaction_data?.oldbalanceDest?.toLocaleString() || '0'}</p>
              <p><strong>Destination New Balance:</strong> ${selectedTransaction.transaction_data?.newbalanceDest?.toLocaleString() || '0'}</p>
            </div>
            
            <div style={detailItemStyles}>
              <h3>Location Information</h3>
              <p><strong>Sender:</strong> {selectedTransaction.location_data?.city || 'Unknown'}, {selectedTransaction.location_data?.region || 'Unknown'}, {selectedTransaction.location_data?.country || 'Unknown'}</p>
              <p><strong>Receiver Country:</strong> {selectedTransaction.transaction_data?.receiver_country || 'Unknown'}</p>
              <p><strong>Using VPN/Proxy:</strong> {
                (selectedTransaction.location_data?.is_vpn || selectedTransaction.location_data?.is_proxy) ? 
                <span style={{color: '#e74c3c', fontWeight: 'bold'}}>Yes</span> : 'No'
              }</p>
              <p><strong>IP Address:</strong> {selectedTransaction.ip_address || 'Unknown'}</p>
            </div>
          </div>
          
          <div>
            <h3>Explanation</h3>
            <pre style={explanationStyles}>{selectedTransaction.explanation || 'No explanation available.'}</pre>
          </div>
          
          <button 
            style={refreshButtonStyles} 
            onClick={() => setSelectedTransaction(null)}
          >
            Return to Transaction List
          </button>
        </div>
      )}
      
      {/* Transactions Table */}
      <div style={transactionsContainerStyles}>
        <h2>All Transactions</h2>
        
        {loading && <p>Loading transactions...</p>}
        
        {error && (
          <div style={{ backgroundColor: '#fceaea', border: '1px solid #e74c3c', padding: '10px', borderRadius: '4px', color: '#e74c3c', marginBottom: '20px' }}>
            {error}
          </div>
        )}
        
        {!loading && !error && transactions.length === 0 && (
          <p>No transactions found. Try adjusting your filters.</p>
        )}
        
        {!loading && !error && transactions.length > 0 && (
          <>
            <table style={tableStyles}>
              <thead>
                <tr>
                  <th style={tableHeaderStyles}>ID</th>
                  <th style={tableHeaderStyles}>Type</th>
                  <th style={tableHeaderStyles}>Amount</th>
                  <th style={tableHeaderStyles}>Location</th>
                  <th style={tableHeaderStyles}>Result</th>
                  <th style={tableHeaderStyles}>Probability</th>
                  <th style={tableHeaderStyles}>Time</th>
                </tr>
              </thead>
              <tbody>
                {transactions.map(tx => (
                  <tr 
                    key={tx.id} 
                    style={{
                      cursor: 'pointer',
                      ...(tx.prediction && tx.prediction.includes('FRAUD') ? fraudRowStyles : 
                         tx.prediction && tx.prediction.includes('HIGH_RISK') ? highRiskRowStyles : null)
                    }}
                    onClick={() => handleRowClick(tx)}
                  >
                    <td style={tableCellStyles}>{tx.id}</td>
                    <td style={tableCellStyles}>{tx.transaction_data?.type || 'Unknown'}</td>
                    <td style={tableCellStyles}>
                      ${tx.transaction_data && tx.transaction_data.amount ? 
                        Number(tx.transaction_data.amount).toLocaleString() : '0'}
                    </td>
                    <td style={tableCellStyles}>
                      {tx.location_data ? 
                        `${tx.location_data.city || 'Unknown'}, ${tx.location_data.country || 'Unknown'}` : 
                        'Unknown'}
                    </td>
                    <td style={tableCellStyles}>{tx.prediction || 'Unknown'}</td>
                    <td style={tableCellStyles}>
                      {typeof tx.probability === 'number' ? 
                        (tx.probability * 100).toFixed(2) + '%' : '0%'}
                    </td>
                    <td style={tableCellStyles}>{formatDate(tx.timestamp)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
            
            {/* Pagination */}
            <div style={paginationStyles}>
              <div>
                Showing {Math.min(filters.offset + 1, totalTransactions)} to {Math.min(filters.offset + transactions.length, totalTransactions)} of {totalTransactions} transactions
              </div>
              <div>
                <button 
                  onClick={handlePrevPage} 
                  disabled={filters.offset === 0}
                  style={filters.offset === 0 ? disabledButtonStyles : paginationButtonStyles}
                >
                  Previous
                </button>
                <button 
                  onClick={handleNextPage} 
                  disabled={filters.offset + filters.limit >= totalTransactions}
                  style={filters.offset + filters.limit >= totalTransactions ? disabledButtonStyles : paginationButtonStyles}
                  className="ml-2"
                >
                  Next
                </button>
              </div>
            </div>
          </>
        )}
      </div>
    </div>
  );
}

export default TransactionHistory;
