import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

function Dashboard() {
  const navigate = useNavigate();
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [recentTransactions, setRecentTransactions] = useState([]);
  const [apiStatus, setApiStatus] = useState({ stats: 'loading', transactions: 'loading' });

  useEffect(() => {
    // Check if API server is available
    axios.get('http://localhost:5000')
      .then(response => {
        console.log('API server is running:', response.data);
      })
      .catch(err => {
        setError(`Backend server may not be running: ${err.message}`);
        setApiStatus({
          stats: 'error',
          transactions: 'error'
        });
        setLoading(false);
        return;
      });

    // Fetch statistics
    axios.get('http://localhost:5000/api/statistics')
      .then(response => {
        console.log('Statistics loaded successfully');
        setStats(response.data);
        setApiStatus(prev => ({...prev, stats: 'loaded'}));
        if (prev => prev.transactions !== 'loading') {
          setLoading(false);
        }
      })
      .catch(err => {
        console.error('Error fetching statistics:', err);
        const errorMessage = err.response ? 
          `Error fetching statistics: ${err.response.status} ${err.response.statusText}` : 
          `Error fetching statistics: ${err.message}`;
        
        setApiStatus(prev => ({...prev, stats: 'error'}));
        setError(errorMessage);
        setLoading(false);
      });

    // Fetch recent transactions
    axios.get('http://localhost:5000/api/transactions', {
      params: { limit: 5 }
    })
      .then(response => {
        console.log('Transactions loaded successfully');
        // Make sure transactions is an array
        const transactions = Array.isArray(response.data.transactions) 
          ? response.data.transactions 
          : [];
        setRecentTransactions(transactions);
        setApiStatus(prev => ({...prev, transactions: 'loaded'}));
        if (prev => prev.stats !== 'loading') {
          setLoading(false);
        }
      })
      .catch(err => {
        console.error('Error fetching recent transactions:', err);
        const errorMessage = err.response ? 
          `Error fetching transactions: ${err.response.status} ${err.response.statusText}` : 
          `Error fetching transactions: ${err.message}`;
        
        setApiStatus(prev => ({...prev, transactions: 'error'}));
        // Don't set error state here to allow showing the dashboard even if transactions fail
      });
  }, []);

  // Function to handle transaction row click
  const handleTransactionClick = (tx) => {
    navigate('/history', { state: { transactionId: tx.id } });
  };

  // Show full screen loading
  if (loading && apiStatus.stats === 'loading' && apiStatus.transactions === 'loading') {
    return (
      <div className="text-center p-6 text-lg text-blue-500">
        <div className="flex justify-center items-center">
          <svg className="animate-spin -ml-1 mr-3 h-8 w-8 text-blue-500" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
            <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
            <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
          </svg>
          <span>Loading dashboard data...</span>
        </div>
      </div>
    );
  }

  // If there's a critical error, show only that
  if (error && (apiStatus.stats === 'error' && apiStatus.transactions === 'error')) {
    return (
      <div className="bg-red-50 border border-red-500 p-4 rounded-lg text-red-600 mb-6">
        <h2 className="text-xl font-bold mb-2">Error</h2>
        <p className="mb-2">{error}</p>
        <p className="mb-4">Make sure your backend server is running at http://localhost:5000</p>
        <div className="mt-4">
          <button 
            onClick={() => window.location.reload()} 
            className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600 transition-colors duration-300"
          >
            Retry
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto px-4">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">Fraud Detection Dashboard</h1>
      
      {/* Show errors if any */}
      {error && (
        <div className="bg-red-50 border border-red-500 p-4 rounded-lg text-red-600 mb-6">
          {error}
        </div>
      )}
      
      {/* Show warning for partial data */}
      {(apiStatus.stats === 'error' || apiStatus.transactions === 'error') && (
        <div className="bg-yellow-50 border border-yellow-500 p-4 rounded-lg text-yellow-800 mb-6">
          <p className="font-semibold">Warning: Some data could not be loaded. Dashboard may show incomplete information.</p>
          {apiStatus.stats === 'error' && <p className="mt-2">Statistics data unavailable.</p>}
          {apiStatus.transactions === 'error' && <p className="mt-2">Transaction data unavailable.</p>}
        </div>
      )}
      
      {/* Statistics Overview */}
      {apiStatus.stats === 'loaded' && stats && (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <div className="bg-white rounded-xl shadow-sm p-6 hover:shadow-md transition-shadow duration-300 border border-gray-100">
            <h3 className="text-gray-600 font-medium mb-2">Total Transactions</h3>
            <p className="text-4xl font-bold text-blue-500">{stats.total_transactions || 0}</p>
          </div>
          
          <div className="bg-white rounded-xl shadow-sm p-6 hover:shadow-md transition-shadow duration-300 border border-gray-100">
            <h3 className="text-gray-600 font-medium mb-2">Fraud Rate</h3>
            <p className="text-4xl font-bold text-blue-500">
              {stats.prediction_counts && stats.prediction_counts['🚨 CONFIRMED_FRAUD'] && stats.total_transactions ? 
                ((stats.prediction_counts['🚨 CONFIRMED_FRAUD'] / stats.total_transactions) * 100).toFixed(2) + '%' : 
                '0%'}
            </p>
          </div>
          
          <div className="bg-white rounded-xl shadow-sm p-6 hover:shadow-md transition-shadow duration-300 border border-gray-100">
            <h3 className="text-gray-600 font-medium mb-2">High Risk</h3>
            <p className="text-4xl font-bold text-blue-500">
              {stats.prediction_counts && stats.prediction_counts['⚠️ HIGH_RISK'] ? 
                stats.prediction_counts['⚠️ HIGH_RISK'] : 
                '0'}
            </p>
          </div>
          
          <div className="bg-white rounded-xl shadow-sm p-6 hover:shadow-md transition-shadow duration-300 border border-gray-100">
            <h3 className="text-gray-600 font-medium mb-2">Average Risk Score</h3>
            <p className="text-4xl font-bold text-blue-500">
              {stats.average_probability !== undefined ? 
                (stats.average_probability * 100).toFixed(2) + '%' : 
                '0%'}
            </p>
          </div>
        </div>
      )}
      
      {/* Transactions Table */}
      {apiStatus.transactions !== 'error' && (
        <div className="bg-white rounded-xl shadow-sm p-6 mb-8 border border-gray-100">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-xl font-bold text-gray-800">Recent Transactions</h2>
            <Link to="/history" className="text-blue-500 hover:text-blue-700 text-sm font-medium">View All</Link>
          </div>
          
          {recentTransactions.length === 0 ? (
            <p className="text-gray-500 py-4">No transactions yet. Try analyzing a test transaction.</p>
          ) : (
            <div className="overflow-x-auto">
              <table className="min-w-full">
                <thead>
                  <tr className="bg-gray-50">
                    <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">ID</th>
                    <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Type</th>
                    <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Amount</th>
                    <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Result</th>
                    <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Probability</th>
                    <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Time</th>
                  </tr>
                </thead>
                <tbody className="divide-y divide-gray-200">
                  {recentTransactions.map(tx => {
                    // Safety checks for transaction properties
                    if (!tx || !tx.transaction_data) {
                      return null;
                    }
                    
                    // Determine row classes based on risk level
                    let rowClasses = "hover:bg-blue-50 cursor-pointer transition-colors duration-150";
                    if (tx.prediction && tx.prediction.includes('FRAUD')) {
                      rowClasses += " bg-red-50";
                    } else if (tx.prediction && tx.prediction.includes('HIGH_RISK')) {
                      rowClasses += " bg-yellow-50";
                    }
                    
                    return (
                      <tr 
                        key={tx.id} 
                        className={rowClasses}
                        onClick={() => handleTransactionClick(tx)}
                      >
                        <td className="px-4 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{tx.id}</td>
                        <td className="px-4 py-4 whitespace-nowrap text-sm text-gray-500">{tx.transaction_data.type || 'Unknown'}</td>
                        <td className="px-4 py-4 whitespace-nowrap text-sm text-gray-500">
                          ${typeof tx.transaction_data.amount === 'number' 
                            ? tx.transaction_data.amount.toLocaleString() 
                            : '0'}
                        </td>
                        <td className="px-4 py-4 whitespace-nowrap text-sm">
                          {tx.prediction && tx.prediction.includes('FRAUD') ? (
                            <span className="px-2 inline-flex text-xs leading-5 font-semibold rounded-full bg-red-100 text-red-800">
                              {tx.prediction || 'Unknown'}
                            </span>
                          ) : tx.prediction && tx.prediction.includes('HIGH_RISK') ? (
                            <span className="px-2 inline-flex text-xs leading-5 font-semibold rounded-full bg-yellow-100 text-yellow-800">
                              {tx.prediction || 'Unknown'}
                            </span>
                          ) : tx.prediction && tx.prediction.includes('NEEDS_REVIEW') ? (
                            <span className="px-2 inline-flex text-xs leading-5 font-semibold rounded-full bg-blue-100 text-blue-800">
                              {tx.prediction || 'Unknown'}
                            </span>
                          ) : (
                            <span className="px-2 inline-flex text-xs leading-5 font-semibold rounded-full bg-green-100 text-green-800">
                              {tx.prediction || 'Unknown'}
                            </span>
                          )}
                        </td>
                        <td className="px-4 py-4 whitespace-nowrap text-sm text-gray-500">
                          {typeof tx.probability === 'number' ? (
                            <div className="flex items-center">
                              <div className="w-16 bg-gray-200 rounded-full h-2.5 mr-2">
                                <div 
                                  className={`h-2.5 rounded-full ${
                                    tx.probability > 0.7 ? 'bg-red-500' : 
                                    tx.probability > 0.3 ? 'bg-yellow-500' : 
                                    'bg-green-500'
                                  }`} 
                                  style={{ width: `${tx.probability * 100}%` }}
                                ></div>
                              </div>
                              <span>{(tx.probability * 100).toFixed(2)}%</span>
                            </div>
                          ) : '0%'}
                        </td>
                        <td className="px-4 py-4 whitespace-nowrap text-sm text-gray-500">
                          {tx.timestamp 
                            ? new Date(tx.timestamp).toLocaleString() 
                            : 'Unknown'}
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
          )}
        </div>
      )}
      
      {/* Quick Actions */}
      <div className="bg-white rounded-xl shadow-sm p-6 mb-8 border border-gray-100">
        <h2 className="text-xl font-bold text-gray-800 mb-4">Quick Actions</h2>
        <div className="flex flex-wrap gap-4">
          <Link to="/test-transaction" className="inline-flex items-center justify-center px-5 py-3 bg-blue-500 text-white font-medium rounded-lg hover:bg-blue-600 transition-colors duration-300 shadow-sm">
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
            </svg>
            Test New Transaction
          </Link>
          <Link to="/location" className="inline-flex items-center justify-center px-5 py-3 bg-blue-500 text-white font-medium rounded-lg hover:bg-blue-600 transition-colors duration-300 shadow-sm">
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
            </svg>
            Location Analysis
          </Link>
          <Link to="/statistics" className="inline-flex items-center justify-center px-5 py-3 bg-blue-500 text-white font-medium rounded-lg hover:bg-blue-600 transition-colors duration-300 shadow-sm">
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
            </svg>
            Statistics
          </Link>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
