import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Header from './components/Header';
import Navigation from './components/Navigation';
import Dashboard from './components/Dashboard';
import LocationDashboard from './components/LocationDashboard';
import TransactionForm from './components/TransactionForm';
import TransactionHistory from './components/TransactionHistory';
import Statistics from './components/Statistics';

function App() {
  const [isMenuOpen, setIsMenuOpen] = useState(true);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <Router>
      <div className="min-h-screen bg-gray-100">
        {/* Header at the top */}
        <Header isMenuOpen={isMenuOpen} toggleMenu={toggleMenu} />

        {/* Main content with navigation and routes */}
        <div className="flex">
          {/* Navigation sidebar */}
          <Navigation isOpen={isMenuOpen} onToggle={toggleMenu} />

          {/* Main content area - adjusted based on menu state */}
          <main className={`flex-1 transition-all duration-300 ${isMenuOpen ? 'ml-64' : 'ml-12'}`}>
            {/* Add top padding to ensure content is below the header */}
            <div className="pt-20 px-6 pb-6">
              <Routes>
                <Route path="/" element={<Dashboard />} />
                <Route path="/location" element={<LocationDashboard />} />
                <Route path="/test-transaction" element={<TransactionForm />} />
                <Route path="/history" element={<TransactionHistory />} />
                <Route path="/statistics" element={<Statistics />} />
              </Routes>
            </div>
          </main>
        </div>
      </div>
    </Router>
  );
}

export default App;
