import React, { useState, useEffect } from 'react';
import { NavLink } from 'react-router-dom';

function Navigation({ onToggle }) {
  const [isOpen, setIsOpen] = useState(true);

  const toggleSidebar = () => {
    const newState = !isOpen;
    setIsOpen(newState);
    
    // Notify parent component if onToggle is provided
    if (onToggle) {
      onToggle(newState);
    }
  };

  // Initialize parent component with sidebar state
  useEffect(() => {
    if (onToggle) {
      onToggle(isOpen);
    }
  }, []);

  return (
    <>
      {/* Hamburger menu button - visible when sidebar is closed */}
      <button 
        onClick={toggleSidebar} 
        className={`fixed z-20 p-2 rounded-r bg-slate-800 text-white ${isOpen ? 'hidden' : 'block top-2 left-0'}`}
        aria-label="Open menu"
      >
        <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
        </svg>
      </button>

      {/* Navigation sidebar */}
      <nav className={`${isOpen ? 'w-64' : 'w-0 -translate-x-full'} h-screen bg-slate-800 text-white fixed left-0 top-0 p-5 shadow-md transition-all duration-300 ease-in-out z-10 overflow-hidden`}>
        <div className="flex justify-between items-center pb-5 mb-5 border-b border-white/10">
          <div>
            <h1 className="text-white mb-1">FRAUDGEN</h1>
            <p className="text-sm opacity-70">Fraud Detection System</p>
          </div>
          
          {/* Close button */}
          <button 
            onClick={toggleSidebar} 
            className="text-white/80 hover:text-white"
            aria-label="Close menu"
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>
        </div>
        
        <ul className="list-none">
          <li className="mb-1">
            <NavLink 
              exact="true" 
              to="/" 
              className={({isActive}) => 
                isActive 
                  ? "block py-2.5 px-5 bg-white/10 text-white border-l-4 border-blue-500" 
                  : "block py-2.5 px-5 text-white/80 hover:bg-white/5 transition-all duration-300"
              }
            >
              Dashboard
            </NavLink>
          </li>
          <li className="mb-1">
            <NavLink 
              to="/test-transaction" 
              className={({isActive}) => 
                isActive 
                  ? "block py-2.5 px-5 bg-white/10 text-white border-l-4 border-blue-500" 
                  : "block py-2.5 px-5 text-white/80 hover:bg-white/5 transition-all duration-300"
              }
            >
              Test Transaction
            </NavLink>
          </li>
          <li className="mb-1">
            <NavLink 
              to="/history" 
              className={({isActive}) => 
                isActive 
                  ? "block py-2.5 px-5 bg-white/10 text-white border-l-4 border-blue-500" 
                  : "block py-2.5 px-5 text-white/80 hover:bg-white/5 transition-all duration-300"
              }
            >
              Transaction History
            </NavLink>
          </li>
          <li className="mb-1">
            <NavLink 
              to="/location" 
              className={({isActive}) => 
                isActive 
                  ? "block py-2.5 px-5 bg-white/10 text-white border-l-4 border-blue-500" 
                  : "block py-2.5 px-5 text-white/80 hover:bg-white/5 transition-all duration-300"
              }
            >
              Location Analysis
            </NavLink>
          </li>
          <li className="mb-1">
            <NavLink 
              to="/statistics" 
              className={({isActive}) => 
                isActive 
                  ? "block py-2.5 px-5 bg-white/10 text-white border-l-4 border-blue-500" 
                  : "block py-2.5 px-5 text-white/80 hover:bg-white/5 transition-all duration-300"
              }
            >
              Statistics
            </NavLink>
          </li>
        </ul>
      </nav>
    </>
  );
}

export default Navigation;
