import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navigation from './components/Navigation';
import Header from './components/Header';
import Dashboard from './components/Dashboard';
import TransactionForm from './components/TransactionForm';
import TransactionHistory from './components/TransactionHistory';
import LocationDashboard from './components/LocationDashboard';
import Statistics from './components/Statistics';
import './index.css';

function App() {
  const [sidebarOpen, setSidebarOpen] = useState(true);

  // Initialize sidebar based on screen size
  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth < 768) {
        setSidebarOpen(false);
      } else {
        setSidebarOpen(true);
      }
    };

    // Set initial state based on current screen size
    handleResize();

    // Add event listener for window resize
    window.addEventListener('resize', handleResize);

    // Clean up on component unmount
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Function to toggle sidebar state
  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  return (
    <Router>
      <div className="flex flex-col min-h-screen bg-gray-100">
        <Header isMenuOpen={sidebarOpen} toggleMenu={toggleSidebar} />
        <Navigation isOpen={sidebarOpen} onToggle={toggleSidebar} />

        <main className={`pt-20 pb-6 ${sidebarOpen ? 'ml-64' : 'ml-12'} transition-all duration-300`}>
          <Routes>
            <Route path="/" element={<Dashboard />} />
            <Route path="/test-transaction" element={<TransactionForm />} />
            <Route path="/location" element={<LocationDashboard />} />
            <Route path="/history" element={<TransactionHistory />} />
            <Route path="/statistics" element={<Statistics />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
