import React, { useState, useEffect, forwardRef } from 'react';
import { NavLink, useLocation } from 'react-router-dom';

const Navigation = forwardRef(({ isOpen, onToggle, className, style, ...props }, ref) => {
  const location = useLocation();
  const [activeSection, setActiveSection] = useState('');
  
  // Update active section based on URL
  useEffect(() => {
    const path = location.pathname;
    setActiveSection(path);
  }, [location]);

  // Combine the className prop with our own classes
  const sidebarClasses = `${className} transition-all duration-300 bg-slate-900 text-white ${
    isOpen ? 'w-64' : 'w-12'
  }`;

  return (
    <>
      {/* Sidebar Container */}
      <nav 
        ref={ref}
        className={sidebarClasses}
        style={{
          ...style,
          height: 'calc(100vh - var(--header-height, 0px))',
          overflow: 'hidden'
        }}
        {...props}
      >
        <div className="h-full overflow-y-auto">
          {/* Main Navigation Items */}
          <div className={`${isOpen ? 'px-4' : 'px-0'} pt-4`}>
            <ul className="space-y-1">
              <li>
                <NavLink 
                  to="/" 
                  className={({isActive}) => 
                    `flex items-center py-2.5 ${isOpen ? 'px-4' : 'px-0 justify-center'} transition-all duration-300 ${
                      isActive 
                        ? 'bg-blue-600/20 text-white border-l-4 border-blue-500 rounded-r-md' 
                        : 'text-white/80 hover:bg-slate-800 rounded-r-md'
                    }`
                  }
                >
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 12l2-2m0 0l7-7 7 7M5 10v10a1 1 0 001 1h3m10-11l2 2m-2-2v10a1 1 0 01-1 1h-3m-6 0a1 1 0 001-1v-4a1 1 0 011-1h2a1 1 0 011 1v4a1 1 0 001 1m-6 0h6" />
                  </svg>
                  {isOpen && <span className="ml-3">Dashboard</span>}
                </NavLink>
              </li>
              <li>
                <NavLink 
                  to="/test-transaction" 
                  className={({isActive}) => 
                    `flex items-center py-2.5 ${isOpen ? 'px-4' : 'px-0 justify-center'} transition-all duration-300 ${
                      isActive 
                        ? 'bg-blue-600/20 text-white border-l-4 border-blue-500 rounded-r-md' 
                        : 'text-white/80 hover:bg-slate-800 rounded-r-md'
                    }`
                  }
                >
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2m-3 7h3m-3 4h3m-6-4h.01M9 16h.01" />
                  </svg>
                  {isOpen && <span className="ml-3">Test Transaction</span>}
                </NavLink>
              </li>
              <li>
                <NavLink 
                  to="/history" 
                  className={({isActive}) => 
                    `flex items-center py-2.5 ${isOpen ? 'px-4' : 'px-0 justify-center'} transition-all duration-300 ${
                      isActive 
                        ? 'bg-blue-600/20 text-white border-l-4 border-blue-500 rounded-r-md' 
                        : 'text-white/80 hover:bg-slate-800 rounded-r-md'
                    }`
                  }
                >
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
                  </svg>
                  {isOpen && <span className="ml-3">Transaction History</span>}
                </NavLink>
              </li>
              <li>
                <NavLink 
                  to="/location" 
                  className={({isActive}) => 
                    `flex items-center py-2.5 ${isOpen ? 'px-4' : 'px-0 justify-center'} transition-all duration-300 ${
                      isActive 
                        ? 'bg-blue-600/20 text-white border-l-4 border-blue-500 rounded-r-md' 
                        : 'text-white/80 hover:bg-slate-800 rounded-r-md'
                    }`
                  }
                >
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
                  </svg>
                  {isOpen && <span className="ml-3">Location Analysis</span>}
                </NavLink>
              </li>
              <li>
                <NavLink 
                  to="/statistics" 
                  className={({isActive}) => 
                    `flex items-center py-2.5 ${isOpen ? 'px-4' : 'px-0 justify-center'} transition-all duration-300 ${
                      isActive 
                        ? 'bg-blue-600/20 text-white border-l-4 border-blue-500 rounded-r-md' 
                        : 'text-white/80 hover:bg-slate-800 rounded-r-md'
                    }`
                  }
                >
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
                  </svg>
                  {isOpen && <span className="ml-3">Statistics</span>}
                </NavLink>
              </li>
            </ul>
          </div>
        </div>

        {/* Toggle button for collapsed sidebar */}
        {!isOpen && (
          <button 
            onClick={onToggle}
            className="absolute bottom-4 left-0 right-0 mx-auto text-white hover:text-blue-300 focus:outline-none flex justify-center"
            aria-label="Expand menu"
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
            </svg>
          </button>
        )}
      </nav>

      {/* Collapse button for expanded sidebar */}
      {isOpen && (
        <button 
          onClick={onToggle}
          className="fixed top-24 right-4 z-50 p-2 bg-slate-700 text-white hover:bg-slate-600 rounded-full shadow-lg"
          aria-label="Collapse menu"
        >
          <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
          </svg>
        </button>
      )}
    </>
  );
});

export default Navigation;
