import React, { useState, useEffect } from 'react';
import axios from 'axios';

function LocationDashboard() {
  const [countryStats, setCountryStats] = useState([]);
  const [vpnProxyStats, setVpnProxyStats] = useState([]);
  const [locationTransactions, setLocationTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedCountry, setSelectedCountry] = useState('all');

  const containerStyles = {
    maxWidth: '1200px',
    margin: '0 auto'
  };

  const dashboardGridStyles = {
    display: 'grid',
    gridTemplateColumns: 'repeat(2, 1fr)',
    gap: '25px',
    marginBottom: '30px'
  };

  const cardStyles = {
    backgroundColor: 'white',
    borderRadius: '8px',
    padding: '20px',
    boxShadow: '0 2px 10px rgba(0, 0, 0, 0.05)'
  };

  const cardHeaderStyles = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '15px'
  };

  const tableStyles = {
    width: '100%',
    borderCollapse: 'collapse'
  };

  const tableHeaderStyles = {
    padding: '12px',
    textAlign: 'left',
    borderBottom: '1px solid #eee',
    backgroundColor: '#f9f9f9',
    fontWeight: '600'
  };

  const tableCellStyles = {
    padding: '12px',
    textAlign: 'left',
    borderBottom: '1px solid #eee'
  };

  const progressBarContainerStyles = {
    height: '16px',
    backgroundColor: '#f0f0f0',
    borderRadius: '8px',
    overflow: 'hidden'
  };

  const progressBarStyles = (value) => ({
    height: '100%',
    width: `${value}%`,
    backgroundColor: value > 50 ? '#e74c3c' : value > 25 ? '#f39c12' : '#2ecc71',
    borderRadius: '8px'
  });

  const filtersStyles = {
    marginBottom: '20px',
    display: 'flex',
    gap: '10px'
  };

  const filterSelectStyles = {
    padding: '8px 12px',
    borderRadius: '4px',
    border: '1px solid #ddd',
    backgroundColor: 'white'
  };

  const mapContainerStyles = {
    height: '400px',
    position: 'relative',
    backgroundColor: '#f9f9f9',
    borderRadius: '8px',
    marginBottom: '25px',
    overflow: 'hidden',
    border: '1px solid #ddd'
  };

  const mapPlaceholderStyles = {
    position: 'absolute',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#f0f8ff',
    color: '#3498db',
    fontSize: '18px'
  };

  const fraudRowStyles = {
    backgroundColor: 'rgba(231, 76, 60, 0.1)'
  };

  const highRiskRowStyles = {
    backgroundColor: 'rgba(243, 156, 18, 0.1)'
  };

  useEffect(() => {
    const fetchLocationData = async () => {
      setLoading(true);
      try {
        // Fetch location statistics
        const statsResponse = await axios.get('http://localhost:5000/api/statistics/locations');
        setCountryStats(statsResponse.data.country_statistics || []);
        setVpnProxyStats(statsResponse.data.vpn_proxy_statistics || []);
        
        // Fetch transactions with location data
        const txQuery = selectedCountry !== 'all' ? `?country=${selectedCountry}&limit=10` : '?limit=10';
        const txResponse = await axios.get(`http://localhost:5000/api/transactions${txQuery}`);
        setLocationTransactions(txResponse.data.transactions || []);
        
        setLoading(false);
      } catch (err) {
        console.error("Error fetching location data:", err);
        setError(err.message);
        setLoading(false);
      }
    };
    
    fetchLocationData();
    
    // Poll for updates every 30 seconds
    const intervalId = setInterval(fetchLocationData, 30000);
    return () => clearInterval(intervalId);
  }, [selectedCountry]);

  const handleCountryChange = (e) => {
    setSelectedCountry(e.target.value);
  };

  if (loading && countryStats.length === 0) {
    return (
      <div style={{ textAlign: 'center', padding: '20px', fontSize: '18px', color: '#3498db' }}>
        Loading location data...
      </div>
    );
  }

  if (error && countryStats.length === 0) {
    return (
      <div style={{ backgroundColor: '#fceaea', border: '1px solid #e74c3c', padding: '10px', borderRadius: '4px', color: '#e74c3c', marginBottom: '20px' }}>
        Error loading location data: {error}
      </div>
    );
  }

  return (
    <div style={containerStyles}>
      <h1>Location-Based Fraud Analysis</h1>
      <p>Monitor fraud patterns by geographic location and detect suspicious location-based activities.</p>
      
      {/* Map Visualization (Placeholder) */}
      <div style={mapContainerStyles}>
        <div style={mapPlaceholderStyles}>
          <p>Transaction Locations Map (Leaflet map will render here)</p>
        </div>
      </div>
      
      {/* Filters */}
      <div style={filtersStyles}>
        <select 
          value={selectedCountry} 
          onChange={handleCountryChange} 
          style={filterSelectStyles}
        >
          <option value="all">All Countries</option>
          {countryStats.map(country => (
            <option key={country.country} value={country.country}>{country.country}</option>
          ))}
        </select>
      </div>
      
      {/* Stats Dashboard */}
      <div style={dashboardGridStyles}>
        {/* Country Fraud Rates */}
        <div style={cardStyles}>
          <div style={cardHeaderStyles}>
            <h2>Fraud by Country</h2>
          </div>
          <table style={tableStyles}>
            <thead>
              <tr>
                <th style={tableHeaderStyles}>Country</th>
                <th style={tableHeaderStyles}>Transactions</th>
                <th style={tableHeaderStyles}>Fraud Rate</th>
                <th style={tableHeaderStyles}>Distribution</th>
              </tr>
            </thead>
            <tbody>
              {countryStats.slice(0, 5).map(country => (
                <tr key={country.country}>
                  <td style={tableCellStyles}>{country.country}</td>
                  <td style={tableCellStyles}>{country.total_transactions}</td>
                  <td style={tableCellStyles}>{country.fraud_percentage}%</td>
                  <td style={tableCellStyles}>
                    <div style={progressBarContainerStyles}>
                      <div style={progressBarStyles(country.fraud_percentage)}></div>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        
        {/* VPN/Proxy Stats */}
        <div style={cardStyles}>
          <div style={cardHeaderStyles}>
            <h2>VPN/Proxy Impact</h2>
          </div>
          <table style={tableStyles}>
            <thead>
              <tr>
                <th style={tableHeaderStyles}>VPN/Proxy Used</th>
                <th style={tableHeaderStyles}>Transactions</th>
                <th style={tableHeaderStyles}>Fraud Rate</th>
                <th style={tableHeaderStyles}>Distribution</th>
              </tr>
            </thead>
            <tbody>
              {vpnProxyStats.map(stat => (
                <tr key={stat.using_vpn_proxy}>
                  <td style={tableCellStyles}>{stat.using_vpn_proxy}</td>
                  <td style={tableCellStyles}>{stat.total_transactions}</td>
                  <td style={tableCellStyles}>{stat.fraud_percentage}%</td>
                  <td style={tableCellStyles}>
                    <div style={progressBarContainerStyles}>
                      <div style={progressBarStyles(stat.fraud_percentage)}></div>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
      
      {/* Recent Location-Based Transactions */}
      <div style={cardStyles}>
        <div style={cardHeaderStyles}>
          <h2>Recent Transactions by Location</h2>
        </div>
        
        {locationTransactions.length === 0 ? (
          <p>No transactions available for the selected location.</p>
        ) : (
          <table style={tableStyles}>
            <thead>
              <tr>
                <th style={tableHeaderStyles}>ID</th>
                <th style={tableHeaderStyles}>Type</th>
                <th style={tableHeaderStyles}>Amount</th>
                <th style={tableHeaderStyles}>Location</th>
                <th style={tableHeaderStyles}>VPN/Proxy</th>
                <th style={tableHeaderStyles}>Result</th>
              </tr>
            </thead>
            <tbody>
              {locationTransactions.map(tx => (
                <tr 
                  key={tx.id} 
                  style={
                    tx.prediction && tx.prediction.includes('FRAUD') ? fraudRowStyles : 
                    tx.prediction && tx.prediction.includes('HIGH_RISK') ? highRiskRowStyles : 
                    null
                  }
                >
                  <td style={tableCellStyles}>{tx.id}</td>
                  <td style={tableCellStyles}>{tx.transaction_data && tx.transaction_data.type}</td>
                  <td style={tableCellStyles}>
                    ${tx.transaction_data && tx.transaction_data.amount && tx.transaction_data.amount.toLocaleString()}
                  </td>
                  <td style={tableCellStyles}>
                    {tx.location_data ? (
                      `${tx.location_data.city || 'Unknown'}, ${tx.location_data.country || 'Unknown'}`
                    ) : (
                      'Location unavailable'
                    )}
                  </td>
                  <td style={tableCellStyles}>
                    {tx.location_data && (tx.location_data.is_vpn || tx.location_data.is_proxy) ? (
                      <span style={{ color: '#e74c3c', fontWeight: 'bold' }}>Yes</span>
                    ) : (
                      'No'
                    )}
                  </td>
                  <td style={tableCellStyles}>{tx.prediction}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default LocationDashboard;
