import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

function Dashboard() {
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [recentTransactions, setRecentTransactions] = useState([]);

  const dashboardStyles = {
    maxWidth: '1200px',
    margin: '0 auto'
  };

  const statsOverviewStyles = {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
    gap: '20px',
    marginBottom: '30px'
  };

  const statCardStyles = {
    backgroundColor: 'white',
    borderRadius: '8px',
    padding: '20px',
    boxShadow: '0 2px 10px rgba(0, 0, 0, 0.05)',
    transition: 'transform 0.3s, box-shadow 0.3s'
  };

  const statValueStyles = {
    fontSize: '32px',
    fontWeight: '600',
    color: '#3498db'
  };

  const sectionHeaderStyles = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '15px'
  };

  const viewAllStyles = {
    color: '#3498db',
    textDecoration: 'none',
    fontSize: '14px'
  };

  const transactionsContainerStyles = {
    backgroundColor: 'white',
    borderRadius: '8px',
    padding: '20px',
    boxShadow: '0 2px 10px rgba(0, 0, 0, 0.05)',
    marginBottom: '30px'
  };

  const tableStyles = {
    width: '100%',
    borderCollapse: 'collapse'
  };

  const tableHeaderStyles = {
    padding: '12px',
    textAlign: 'left',
    borderBottom: '1px solid #eee',
    backgroundColor: '#f9f9f9',
    fontWeight: '600'
  };

  const tableCellStyles = {
    padding: '12px',
    textAlign: 'left',
    borderBottom: '1px solid #eee'
  };

  const fraudRowStyles = {
    backgroundColor: 'rgba(231, 76, 60, 0.1)'
  };

  const highRiskRowStyles = {
    backgroundColor: 'rgba(243, 156, 18, 0.1)'
  };

  const quickActionsStyles = {
    backgroundColor: 'white',
    borderRadius: '8px',
    padding: '20px',
    boxShadow: '0 2px 10px rgba(0, 0, 0, 0.05)'
  };

  const actionButtonsStyles = {
    display: 'flex',
    gap: '15px'
  };

  const actionButtonStyles = {
    display: 'inline-block',
    padding: '12px 20px',
    backgroundColor: '#3498db',
    color: 'white',
    borderRadius: '4px',
    textDecoration: 'none',
    fontWeight: '500',
    transition: 'background-color 0.3s'
  };

  useEffect(() => {
    // Fetch statistics
    axios.get('http://localhost:5000/api/statistics')
      .then(response => {
        setStats(response.data);
        setLoading(false);
      })
      .catch(err => {
        setError(err.message);
        setLoading(false);
      });

    // Fetch recent transactions
    axios.get('http://localhost:5000/api/transactions?limit=5')
      .then(response => {
        setRecentTransactions(response.data.transactions);
      })
      .catch(err => {
        console.error('Error fetching recent transactions:', err);
      });
  }, []);

  if (loading) return <div style={{ textAlign: 'center', padding: '20px', fontSize: '18px', color: '#3498db' }}>Loading statistics...</div>;
  if (error) return <div style={{ backgroundColor: '#fceaea', border: '1px solid #e74c3c', padding: '10px', borderRadius: '4px', color: '#e74c3c', marginBottom: '20px' }}>Error: {error}</div>;

  return (
    <div style={dashboardStyles}>
      <h1>Fraud Detection Dashboard</h1>
      
      {stats && (
        <div style={statsOverviewStyles}>
          <div style={statCardStyles}>
            <h3>Total Transactions</h3>
            <p style={statValueStyles}>{stats.total_transactions || 0}</p>
          </div>
          
          <div style={statCardStyles}>
            <h3>Fraud Rate</h3>
            <p style={statValueStyles}>
              {stats.prediction_counts && stats.prediction_counts['üö® CONFIRMED_FRAUD'] && stats.total_transactions ? 
                ((stats.prediction_counts['üö® CONFIRMED_FRAUD'] / stats.total_transactions) * 100).toFixed(2) + '%' : 
                '0%'}
            </p>
          </div>
          
          <div style={statCardStyles}>
            <h3>High Risk</h3>
            <p style={statValueStyles}>
              {stats.prediction_counts && stats.prediction_counts['‚ö†Ô∏è HIGH_RISK'] ? 
                stats.prediction_counts['‚ö†Ô∏è HIGH_RISK'] : 
                '0'}
            </p>
          </div>
          
          <div style={statCardStyles}>
            <h3>Average Risk Score</h3>
            <p style={statValueStyles}>
              {stats.average_probability ? 
                (stats.average_probability * 100).toFixed(2) + '%' : 
                '0%'}
            </p>
          </div>
        </div>
      )}
      
      <div style={transactionsContainerStyles}>
        <div style={sectionHeaderStyles}>
          <h2>Recent Transactions</h2>
          <Link to="/history" style={viewAllStyles}>View All</Link>
        </div>
        
        {recentTransactions.length === 0 ? (
          <p>No transactions yet. Try analyzing a test transaction.</p>
        ) : (
          <table style={tableStyles}>
            <thead>
              <tr>
                <th style={tableHeaderStyles}>ID</th>
                <th style={tableHeaderStyles}>Type</th>
                <th style={tableHeaderStyles}>Amount</th>
                <th style={tableHeaderStyles}>Result</th>
                <th style={tableHeaderStyles}>Probability</th>
                <th style={tableHeaderStyles}>Time</th>
              </tr>
            </thead>
            <tbody>
              {recentTransactions.map(tx => (
                <tr 
                  key={tx.id} 
                  style={
                    tx.prediction.includes('FRAUD') ? fraudRowStyles : 
                    tx.prediction.includes('HIGH_RISK') ? highRiskRowStyles : 
                    null
                  }
                >
                  <td style={tableCellStyles}>{tx.id}</td>
                  <td style={tableCellStyles}>{tx.transaction_data.type}</td>
                  <td style={tableCellStyles}>${tx.transaction_data.amount.toLocaleString()}</td>
                  <td style={tableCellStyles}>{tx.prediction}</td>
                  <td style={tableCellStyles}>{(tx.probability * 100).toFixed(2)}%</td>
                  <td style={tableCellStyles}>{new Date(tx.timestamp).toLocaleString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
      
      <div style={quickActionsStyles}>
        <h2>Quick Actions</h2>
        <div style={actionButtonsStyles}>
          <Link to="/test-transaction" style={actionButtonStyles}>
            Test New Transaction
          </Link>
          <Link to="/statistics" style={actionButtonStyles}>
            View Detailed Statistics
          </Link>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
