import React, { useEffect, useState, useRef } from 'react';

function LocationMap({ transactions }) {
  const [mapLoaded, setMapLoaded] = useState(false);
  const leafletMapRef = useRef(null);
  const markersRef = useRef([]);
  const mapContainerRef = useRef(null);
  const leafletScriptRef = useRef(null);
  const leafletStyleRef = useRef(null);

  const mapContainerStyles = {
    height: '400px',
    width: '100%',
    borderRadius: '8px',
    overflow: 'hidden'
  };

  const loadingStyles = {
    height: '100%',
    display: 'flex', 
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#f9f9f9',
    color: '#3498db',
    fontSize: '16px'
  };

  // Initialize the map only once
  useEffect(() => {
    // Create a unique ID for the map container to avoid conflicts
    const mapId = `map-${Math.random().toString(36).substr(2, 9)}`;
    
    // Set the ID on the container
    if (mapContainerRef.current) {
      mapContainerRef.current.id = mapId;
    }

    // Clean up function to remove any previously loaded scripts/styles
    const cleanup = () => {
      if (leafletMapRef.current) {
        leafletMapRef.current.remove();
        leafletMapRef.current = null;
      }
      
      // Remove markers
      if (markersRef.current.length > 0) {
        markersRef.current.forEach(marker => {
          if (marker && marker.remove) {
            marker.remove();
          }
        });
        markersRef.current = [];
      }
      
      // Remove Leaflet script if it exists
      if (leafletScriptRef.current && document.body.contains(leafletScriptRef.current)) {
        document.body.removeChild(leafletScriptRef.current);
        leafletScriptRef.current = null;
      }
      
      // Remove Leaflet style if it exists
      if (leafletStyleRef.current && document.head.contains(leafletStyleRef.current)) {
        document.head.removeChild(leafletStyleRef.current);
        leafletStyleRef.current = null;
      }
    };

    // Only load the map if not already loaded
    if (!mapLoaded && !leafletMapRef.current) {
      // Clean up any previous instances
      cleanup();
      
      // Dynamically load Leaflet CSS
      const linkElement = document.createElement('link');
      linkElement.rel = 'stylesheet';
      linkElement.href = 'https://unpkg.com/leaflet@1.7.1/dist/leaflet.css';
      document.head.appendChild(linkElement);
      leafletStyleRef.current = linkElement;

      // Dynamically load Leaflet JS
      const script = document.createElement('script');
      script.src = 'https://unpkg.com/leaflet@1.7.1/dist/leaflet.js';
      script.integrity = 'sha512-XQoYMqMTK8LvdxXYG3nZ448hOEQiglfqkJs1NOQV44cWnUrBc8PkAOcXy20w0vlaXaVUearIOBhiXZ5V3ynxwA==';
      script.crossOrigin = '';
      
      script.onload = () => {
        // Initialize map after Leaflet is loaded
        if (window.L && mapContainerRef.current) {
          // Create map instance
          const map = window.L.map(mapId).setView([40.7282, -74.0776], 2);
          
          // Add tile layer
          window.L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
            attribution: 'Â© OpenStreetMap contributors'
          }).addTo(map);
          
          leafletMapRef.current = map;
          setMapLoaded(true);
        }
      };
      
      document.body.appendChild(script);
      leafletScriptRef.current = script;
    }

    // Clean up on component unmount
    return cleanup;
  }, []);
  
  // Update markers when transactions change
  useEffect(() => {
    if (mapLoaded && leafletMapRef.current && transactions && window.L) {
      // Remove existing markers
      markersRef.current.forEach(marker => {
        if (marker && marker.remove) {
          marker.remove();
        }
      });
      markersRef.current = [];
      
      // Create new markers
      const newMarkers = [];
      const validLocations = [];
      
      transactions.forEach(tx => {
        if (tx.location_data && tx.location_data.latitude && tx.location_data.longitude) {
          try {
            // Convert latitude/longitude to numbers to avoid errors
            const lat = parseFloat(tx.location_data.latitude);
            const lng = parseFloat(tx.location_data.longitude);
            
            // Skip if invalid coordinates
            if (isNaN(lat) || isNaN(lng)) return;
            
            // Skip if coordinates are out of range
            if (lat < -90 || lat > 90 || lng < -180 || lng > 180) return;
            
            // Add to valid locations for bounds
            validLocations.push([lat, lng]);
            
            // Determine marker color based on transaction risk
            const markerColor = tx.prediction && tx.prediction.includes('FRAUD') ? 'red' : 
                              tx.prediction && tx.prediction.includes('HIGH_RISK') ? 'orange' : 
                              tx.prediction && tx.prediction.includes('NEEDS_REVIEW') ? 'yellow' : 
                              'green';
            
            // Create custom marker icon
            const markerIcon = window.L.divIcon({
              className: 'custom-marker',
              html: `<div style="width: 16px; height: 16px; border-radius: 50%; background-color: ${markerColor}; border: 2px solid white;"></div>`,
              iconSize: [20, 20],
              iconAnchor: [10, 10]
            });
            
            // Create marker
            const marker = window.L.marker(
              [lat, lng], 
              { icon: markerIcon }
            ).addTo(leafletMapRef.current);
            
            // Format amount safely
            const amount = tx.transaction_data && tx.transaction_data.amount && 
                          typeof tx.transaction_data.amount === 'number' ? 
                          tx.transaction_data.amount.toLocaleString() : '0';
            
            // Add popup with transaction details
            marker.bindPopup(`
              <div style="min-width: 200px;">
                <h3 style="margin: 0 0 5px 0;">${tx.prediction || 'Unknown'}</h3>
                <p style="margin: 0 0 3px 0;"><strong>Amount:</strong> $${amount}</p>
                <p style="margin: 0 0 3px 0;"><strong>Type:</strong> ${tx.transaction_data?.type || 'Unknown'}</p>
                <p style="margin: 0 0 3px 0;"><strong>Location:</strong> ${tx.location_data.city || 'Unknown'}, ${tx.location_data.region || 'Unknown'}, ${tx.location_data.country || 'Unknown'}</p>
                ${tx.location_data.is_vpn || tx.location_data.is_proxy ? 
                  '<p style="margin: 0; color: #e74c3c;"><strong>VPN/Proxy detected</strong></p>' : ''}
              </div>
            `);
            
            newMarkers.push(marker);
          } catch (err) {
            console.error("Error creating marker:", err);
          }
        }
      });
      
      markersRef.current = newMarkers;
      
      // Adjust map view to fit markers if there are any
      if (validLocations.length > 0) {
        try {
          // Check if we have at least 2 distinct locations
          const uniqueLocations = new Set(validLocations.map(loc => `${loc[0]},${loc[1]}`));
          
          if (uniqueLocations.size >= 2) {
            // Multiple distinct locations, create bounds
            const bounds = window.L.latLngBounds(validLocations);
            leafletMapRef.current.fitBounds(bounds, { padding: [50, 50] });
          } else {
            // Only one location, just center on it
            const center = validLocations[0];
            leafletMapRef.current.setView(center, 12);
          }
        } catch (err) {
          console.error("Error adjusting map view:", err);
          
          // Fallback to default view if bounds fails
          leafletMapRef.current.setView([40.7282, -74.0776], 2);
        }
      }
    }
  }, [mapLoaded, transactions]);

  return (
    <div ref={mapContainerRef} style={mapContainerStyles}>
      {!mapLoaded && (
        <div style={loadingStyles}>
          Loading map...
        </div>
      )}
    </div>
  );
}

export default LocationMap;
