import React from 'react';
import { NavLink } from 'react-router-dom';

function Navigation() {
  const navStyles = {
    width: '250px',
    height: '100vh',
    backgroundColor: '#2c3e50',
    color: 'white',
    position: 'fixed',
    left: 0,
    top: 0,
    padding: '20px 0',
    boxShadow: '2px 0 5px rgba(0, 0, 0, 0.1)'
  };

  const logoStyles = {
    padding: '0 20px 20px 20px',
    borderBottom: '1px solid rgba(255, 255, 255, 0.1)',
    marginBottom: '20px'
  };

  const logoTextStyles = {
    color: 'white',
    marginBottom: '5px'
  };

  const logoSubtitleStyles = {
    fontSize: '14px',
    opacity: 0.7
  };

  const navListStyles = {
    listStyle: 'none'
  };

  const navItemStyles = {
    marginBottom: '5px'
  };

  const navLinkStyles = {
    display: 'block',
    padding: '10px 20px',
    color: 'rgba(255, 255, 255, 0.8)',
    textDecoration: 'none',
    transition: 'all 0.3s'
  };

  const navLinkActiveStyles = {
    ...navLinkStyles,
    backgroundColor: 'rgba(255, 255, 255, 0.1)',
    color: 'white',
    borderLeft: '4px solid #3498db'
  };

  return (
    <nav style={navStyles}>
      <div style={logoStyles}>
        <h1 style={logoTextStyles}>FRAUDGEN</h1>
        <p style={logoSubtitleStyles}>Fraud Detection System</p>
      </div>
      <ul style={navListStyles}>
        <li style={navItemStyles}>
          <NavLink exact to="/" style={navLinkStyles} activeStyle={navLinkActiveStyles}>
            Dashboard
          </NavLink>
        </li>
        <li style={navItemStyles}>
          <NavLink to="/test-transaction" style={navLinkStyles} activeStyle={navLinkActiveStyles}>
            Test Transaction
          </NavLink>
        </li>
        <li style={navItemStyles}>
          <NavLink to="/history" style={navLinkStyles} activeStyle={navLinkActiveStyles}>
            Transaction History
          </NavLink>
        </li>
        <li style={navItemStyles}>
          <NavLink to="/statistics" style={navLinkStyles} activeStyle={navLinkActiveStyles}>
            Statistics
          </NavLink>
        </li>
      </ul>
    </nav>
  );
}

export default Navigation;
